'use strict';

var touchup = require('./');

var sourceURL = 'data:image/png;base64,';

var showImage = function(url, note) {
  var image = document.createElement('img');
  image.setAttribute('src', url);
  image.addEventListener('load', function() {
    document.body.appendChild(image);
    document.body.appendChild(document.createTextNode(note));
    document.body.appendChild(document.createElement('br'));
  });
};

var imageElement = document.createElement('img');
imageElement.setAttribute('src', sourceURL);
imageElement.addEventListener('load', function() {
  document.body.appendChild(imageElement);
  document.body.appendChild(document.createTextNode('source'));
  document.body.appendChild(document.createElement('br'));

  showImage(touchup.repeat(imageElement, 2, 2), 'repeat(2,2)');
  showImage(touchup.scale(imageElement, 2.0, 2.0), 'scale(2,2)');
  showImage(touchup.repeat(imageElement, 6, 4), 'repeat(6,4)');
  showImage(touchup.scale(imageElement, 3.0, 0.5), 'scale(3,0.5)');
});

